query.sql:
DECLARE
  p_sn_id NUMBER := :SNAP_ID;
  p_inst  NUMBER := :INSTANCE_NUMBER;
  p_sqlid VARCHAR2(20) := :SQL_ID;
BEGIN
  dbms_output.enable(NULL);
  dbms_output.put_line('<pre>');
  IF (p_sn_id IS NOT NULL) THEN
    dbms_output.put_line('===============================================================================');
    dbms_output.put_line('Historical Plans for SQL_ID '|| p_sqlid);
    dbms_output.put_line('Instance '|| p_inst);
    dbms_output.put_line('SNAP_ID  '|| p_sn_id);
    dbms_output.put_line('===============================================================================');
    dbms_output.put_line('');
    dbms_output.put_line('SQL Text');
    dbms_output.put_line('--------');
    FOR c IN( select SQL_TEXT
        from perfstat.STATS$SQLTEXT where sql_id=p_sqlid order by piece
    ) LOOP
      dbms_output.put_line( c.sql_text );
    END LOOP;
    FOR h IN(select plan_hash_value phv
               from stats$sql_plan_usage spu
              where spu.sql_id  = p_sqlid
                and spu.snap_id = p_sn_id
                and spu.instance_number = p_inst
                and spu.plan_hash_value > 0
              group by plan_hash_value
              order by min(spu.LAST_ACTIVE_TIME)
    ) LOOP
      dbms_output.put_line('');
      dbms_output.put_line('================================================================================');
      dbms_output.put_line('Plan_Hash_Value = '||h.phv);
      FOR c IN(
        Select *
          from table(dbms_xplan.display(
      table_name   => 'perfstat.stats$sql_plan',
      statement_id => null,
      format       => 'ALL -predicate -note',
      filter_preds => 'plan_hash_value = '||h.phv
    ))
      ) LOOP
        dbms_output.put_line( c.plan_table_output );
      END LOOP;
    END LOOP;
  ELSE
    dbms_output.put_line('Select a Snapshot');
  END IF;
  dbms_output.put_line('</pre>');
END;

set pages 50000 lines 180 feedback off trimspool on
        select *
          from table(dbms_xplan.display_cursor( :SQL_ID, null, 'ADVANCED ALLSTATS LAST'));
